package euler;

import static org.junit.Assert.assertEquals;

import java.util.ArrayList;

import org.junit.Test;

public class LargestProductInASeriesTest {

	@Test
	public void makeArrayListFromStringTest() {
		ArrayList<Integer> arrayList = new ArrayList<Integer>();
		arrayList = LargestProductInASeries.numberAsArrayList("0123456789");
		assertEquals(arrayList.get(0), (Integer) 0);
	}
	
	@Test
	public void checkSuperLongStringTest() {
		ArrayList<Integer> arrayList = new ArrayList<Integer>();
		arrayList = LargestProductInASeries.numberAsArrayList("");
		assertEquals(arrayList.get(0), (Integer) 7);//first digit
		assertEquals(arrayList.get(999), (Integer) 0);//last digit
	}
	
	@Test
	public void checkWindowTest() {
		ArrayList<Integer> arrayList = new ArrayList<Integer>();
		arrayList = LargestProductInASeries.numberAsArrayList("0123456789");
		int product = LargestProductInASeries.checkWindow(arrayList, 2, 4);
		assertEquals(product, 6);
	}
	
	@Test
	public void checkWindowTest2() {
		ArrayList<Integer> arrayList = new ArrayList<Integer>();
		arrayList = LargestProductInASeries.numberAsArrayList("9989");
		int product = LargestProductInASeries.checkWindow(arrayList, 0, 4);
		assertEquals(product, 5832);
	}
	
	@Test
	public void productTest(){
		ArrayList<Integer> arrayList = new ArrayList<Integer>();
		arrayList = LargestProductInASeries.numberAsArrayList("0123456789");
		int largestProduct = LargestProductInASeries.product(arrayList, 2);
		assertEquals(largestProduct, 9*8);
		int largestProduct3 = LargestProductInASeries.product(arrayList, 3);
		assertEquals(largestProduct3, 9*8*7);
		int largestProduct10 = LargestProductInASeries.product(arrayList, 10);
		assertEquals(largestProduct10, 0);
		int largestProduct9 = LargestProductInASeries.product(arrayList, 9);
		assertEquals(largestProduct9, 9*8*7*6*5*4*3*2*1);
	}
	
	@Test
	public void productTest2() {
		ArrayList<Integer> arrayList = new ArrayList<Integer>();
		arrayList = LargestProductInASeries.numberAsArrayList("");
		int largestProduct4 = LargestProductInASeries.product(arrayList, 4);
		assertEquals(largestProduct4, 5832);
	}
	
	@Test
	public void productTest3() {
		ArrayList<Integer> arrayList = new ArrayList<Integer>();
		arrayList = LargestProductInASeries.numberAsArrayList("7316717653133");
		int largestProduct5 = LargestProductInASeries.product(arrayList, 13);
		assertEquals(largestProduct5, 7*3*1*6*7*1*7*6*5*3*1*3*3);
	}
}

